mapper.select:
select

  distinct


from ba_defined_code




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select 

from ba_defined_code
where defined_code_type = #{definedCodeType,jdbcType=VARCHAR}
  and defined_code = #{definedCode,jdbcType=VARCHAR}

mapper.delete:
delete from ba_defined_code
where defined_code_type = #{definedCodeType,jdbcType=VARCHAR}
  and defined_code = #{definedCode,jdbcType=VARCHAR}
